import { useParams, NavLink } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { PokemonCurrentProvider } from '../../provider/PokemonCurrentProvider.tsx';
import { useSearchContext } from '../../provider/SearchProvider.tsx';

import PokemonAbility from './Figure/PokemonAbility.tsx';
import PokemonType from './Figure/PokemonType.tsx';
import PokemonStats from './Figure/PokemonStats.tsx';
import NotFound from '../NotFound/NotFound.tsx';

import { PokemonInformation } from '../../interfaces/Pokemon.ts';
import clearImageUrl from '../../helper/clearImageUrl.tsx';

import styles from './PokemonCurrent.module.css';

function PokemonCurrent() {
  const { page, name } = useParams();
  const [isPokemonExist, setIsPokemonExist] = useState<boolean>(true);
  const [currentPokemon, setCurrentPokemon] = useState<PokemonInformation>({
    id: 0,
    name: '',
    image:
      'data:image/png;base64,',
    abilities: [],
    stats: [],
    types: [],
  });
  const { updateSearchRequest } = useSearchContext();

  useEffect((): void => {
    fetch(`https://pokeapi.co/api/v2/pokemon/${name}`)
      .then((response): Promise<PokemonInformation> => response.json())
      .then((json: PokemonInformation): void => {
        const sprites = json.sprites?.other?.['official-artwork']?.front_default;
        setIsPokemonExist(true);

        setCurrentPokemon({
          id: json.id,
          name: json.name,
          abilities: json.abilities,
          image: clearImageUrl(sprites),
          stats: json.stats,
          types: json.types,
        });
      })
      .catch((): void => setIsPokemonExist(false));
  }, [name]);

  return (
    <PokemonCurrentProvider
      value={{
        types: currentPokemon.types,
        abilities: currentPokemon.abilities,
        stats: currentPokemon.stats,
      }}
    >
      <div className={styles.wrapper}>
        {isPokemonExist && (
          <>
            <img
              className={styles.image}
              src={currentPokemon.image}
              width="300"
              height="300"
              alt={currentPokemon.name}
            />
            <div>
              <h4 className={styles.title}>{currentPokemon.name}</h4>
              <PokemonType />
              <PokemonAbility />
              <PokemonStats />
            </div>
          </>
        )}

        {!isPokemonExist && <NotFound />}

        <NavLink
          className={styles.close}
          to={`/page/${page && +page}`}
          onClick={() => updateSearchRequest('')}
        >
          +
        </NavLink>
      </div>
    </PokemonCurrentProvider>
  );
}

export default PokemonCurrent;
